INSERT INTO `review` (`id`, `comment`, `createdAt`, `productId`, `userId`, `stars`, `type`) VALUES
	(0, 'Đôi mắt có màu đỏ tươi như lồng đèn lúc này cũng đã xuất hiện một ít sự né tránh và mệt mỏi. Có lẽ đối mặt với loại cường địch như Thôn Thiên Mãng, Tuyết Ma Thiên Viên trong tình trang suy yếu đã sắp hoàn toàn mất ý chí chiến đấu.\r\n', '2023-02-28 15:55:31', 17, 51, 5, ''),
	(1, 'Gần 1 rưỡi sáng ngày 14/10 đặt. Tưởng hết mà ai dè ăn may đặt được 1 con hên ghê. Giá 32.9 thì cũng tốt hơn ở ngoài khá nhiều. Sáng nay Tiki đã giao rồi. Máy đẹp lắm, đóng gói cũng rất chắc chắn nữa. Mọi thứ đều rất hài lòng 🥰 hẹn 21/10 mới giao mà sáng nay tới luôn rồi 😘 Máy mới cài sẵn iOS 16.0.1 nhé', '2023-02-27 14:21:46', 2, 53, 4, ''),
	(2, 'Sản phẩm chính hãng, nguyên seal, nhưng chưa hài lòng việc vận chuyển, đáng lẽ ở tỉnh lẻ nên vận chuyển từ đêm ngày 13/10 để sáng 14/10 nhận hàng sẽ tốt hơn. Và với mặt hàng này nên làm việc riêng với vận chuyển để riêng sản phẩm ra giao cho khách hộp mới nguyên không móp méo dù đã bọc khá kỹ.', '2023-02-27 14:22:46', 2, 54, 3, ''),
	(5, 'Cảm thấy thật có lỗi khi comment đánh giá của bạn BẢO NGÔ là người quen mới mua được,sau đó vào coi thử thì may mắn mình lại đặt được 14 Pro Max 256GB giá chỉ 32tr, cám ơn tiki, quá uy tín', '2023-02-28 13:25:08', 1, 54, 5, 'vàng'),
	(6, 'Sản phẩm đóng gói cẩn thận, giao nhanh, đúng. Sau vài ngày sử dụng chưa có lỗi, sản phẩm tốt. Hài lòng về sản phẩm và chất lượng dịch vụ của tiki.', '2023-02-28 13:26:42', 1, 55, 3, 'xanh'),
	(7, 'Từ trên thạch đài hạ xuống, Tiêu Viêm lại dừng bên cạnh tảng đá xanh. Ánh mắt hắn nhìn về số chất lỏng màu trắng sữa trong rãnh đá. Tuy nói chất lỏng này đã bị pha loãng đi, nhưng hiệu quả lại không tệ chút nào. Cho dù không đạt được tẩy tủy luyện cốt thì hiệu quả tôi luyện cơ thể cũng không ít', '2023-02-28 15:39:03', 22, 50, 5, ''),
	(8, 'Thứ này cũng nên lấy một ít, sau này, lúc luyện chế đan dược đặt biệt có thể dùng tới.\r\n\r\nÂm thanh Dược Lão vang lên trên đầu hắn.', '2023-02-28 15:39:32', 22, 49, 4, ''),
	(9, 'Nghe vậy, Tiêu Viêm lại lấy ra hai bình ngọc từ trong nạp giới, thật cẩn thận đem Địa Tâm Thối Thể Nhũ màu trắng ngà thu vào bình. Sau khi hai bình ngọc đã đầy, chất lỏng màu trắng sữa trong rãnh cũng đã vơi đi hơn phân nửa.', '2023-02-28 15:49:58', 3, 49, 3, ''),
	(10, 'Liếc mắt nhìn lượng nhũ dịch còn lại trong rãnh, Tiêu Viêm hơi suy tư hơn nữa cũng không đem nó lấy hết. Sau này nói không chừng đám người Lâm Tu Nhai sẽ đến để đoạt Địa Tâm Thối Thể Nhũ. ', '2023-02-28 15:50:16', 3, 48, 4, ''),
	(11, 'Nếu đến cuối cùng, vất vả cả nữa ngày mà không thu hoạch được gì, khó tránh khỏi bọn chúng sẽ hoài nghi rằng đã có người hạ thủ trước, lấy đi hết số bảo bối này. Mà biết được nơi này có kỳ bảo cũng chỉ có bọn chúng và Tiêu Viêm. ', '2023-02-28 15:51:09', 4, 56, 3, ''),
	(12, 'Mặc dù hắn không sợ bọn họ hoài nghi nhưng dù sao thì "Địa Tâm Thối Thể Nhũ" này vẫn là do Hàn Nguyệt phát hiện trước, bản thân vô tình chiếm được tiện nghi lớn, nếu cứ giữ lòng tham muốn ôm hết số bảo vật thì cũng có chút quá đáng.', '2023-02-28 15:51:14', 4, 51, 4, ''),
	(13, 'Ý niệm lướt qua trong lòng Tiêu Viêm, hắn liền đem hai bình Địa Tâm Thối Thể Nhũ đã bị pha loãng thu vào nạp giới. Sau đó, không hề nán lại, xoay người hướng theo con đường lúc trước đến đây bay nhanh', '2023-02-28 15:51:42', 5, 50, 5, ''),
	(14, 'Vì đã quen thuộc với đường đi, nên lần này thời gian hắn đi ra đã giảm đi không ít. Chỉ cần hai mươi phút đã ra khỏi sơn động.', '2023-02-28 15:51:43', 5, 45, 3, ''),
	(15, 'Vỗ mạnh hai cánh, Tiêu Viêm bay vút ra ngoài cốc, ánh mắt nhìn về phía xa. Trong bóng tối nhưng hắn vẫn thấy được hào quang bảy màu lúc ẩn lúc hiện ngoài cốc, một sự bùng nổ năng lượng như sấm vang chớp giật.', '2023-02-28 15:52:38', 6, 50, 4, ''),
	(16, 'Nhìn thấy thất thải quang mang vẫn sáng chói như cũ, Tiêu Viêm mới nhẹ nhõm được một ít.', '2023-02-28 15:52:43', 6, 49, 5, ''),
	(17, 'Đôi cánh vỗ nhanh hết tốc lực, thân hình hắn hóa thành một bóng đen, không một tiếng động xuyên vun vút qua màn đêm. Một lúc lâu sau, thân hình dừng lại, hắn đã xuất hiện ở lưng chừng không của cốc khẩu. ', '2023-02-28 15:53:03', 7, 53, 4, ''),
	(18, 'Ánh mắt hướng về chiến trường trước mặt, không khỏi cảm thấy líu lưỡi.', '2023-02-28 15:53:05', 7, 54, 4, ''),
	(19, 'Cốc khẩu lúc này cơ hồ đã bi tàn phá đến không ra hình dạng gì cả. Mặt đất vốn bằng phẳng bị những lỗ hỏng gồ ghề phủ kín.', '2023-02-28 15:53:23', 8, 48, 3, ''),
	(20, 'Những tảng đá lớn từ trên vách núi rơi rụng xuống rừng rậm hai bên, khiến nó bị phá hoại không ít. Cây cối gãy ngang che lấp hơn một nửa thông đạo nơi cốc khẩu.', '2023-02-28 15:53:26', 8, 46, 5, ''),
	(21, 'Mà bên trong chiến trường, Thôn Thiên Mãng vẫn như cũ, cuộn thân thể khổng lồ chiếm cứ bầu trời, thất thải quang mang trong cơ thể cuồn cuộn tràn ra, uy áp mạnh mẽ làm ma thú đang ẩn núp trong phạm vi mười dặm đều phát run. ', '2023-02-28 15:53:47', 9, 55, 4, ''),
	(22, 'Mặc dù khí thê Thôn Thiên Mãng vẫn mạnh mẽ như cũ, nhưng nếu nhìn thật kỹ vẫn có thể thấy phía trên số vảy rắn bảy màu hiện lên một số vết cào rất sâu. Có lẽ trong lúc chiến đấu, Tuyết Ma Thiên Viên củng không phải ở vào thế thất bại hoàn toàn.', '2023-02-28 15:54:04', 9, 54, 3, ''),
	(23, 'Ánh mắt lườm xuống dưới, khi trông thấy Tuyết Ma Thiên Viên, khuôn mặt Tiêu Viêm nhịn không được hiện lên vẻ kinh ngạc.', '2023-02-28 15:54:58', 10, 53, 4, ''),
	(24, 'Lúc này, cả người lông mao màu trắng như tuyết của Tuyết Ma Thiên Viên đã bị xà toan có tính ăn mòn mãnh liệt của Thôn Thiên Mãng xâm thực hơn một nửa.', '2023-02-28 15:55:09', 10, 46, 5, ''),
	(25, 'Máu tươi giàn giụa trên cái đầu to lớn, khiến cho nó vốn đã có vẻ rất khó nhìn càng thêm dữ tơn. Trên cơ thể cao lớn cũng có không ít dấu vết bị năng lượng mạnh mẽ va chạm.', '2023-02-28 15:55:24', 11, 54, 5, ''),
	(27, 'Đôi mắt có màu đỏ tươi như lồng đèn lúc này cũng đã xuất hiện một ít sự né tránh và mệt mỏi. Có lẽ đối mặt với loại cường địch như Thôn Thiên Mãng, Tuyết Ma Thiên Viên trong tình trang suy yếu đã sắp hoàn toàn mất ý chí chiến đấu.\r\n', '2023-02-28 15:56:12', 11, 55, 4, ''),
	(28, 'So sánh với bộ dáng thê thảm của Tuyết Ma Thiên Viên thì thương thế của Thôn Thiên Mãng xem ra không có gì nghiêm trọng.', '2023-02-28 15:57:14', 12, 48, 5, ''),
	(29, 'Không ngờ được tiểu gia hỏa này lại cường hãn như vậy, quả không hổ danh thượng cổ dị thú.', '2023-02-28 15:57:18', 12, 53, 3, ''),
	(30, 'Nhìn sự chênh lệch về vết thương giữa hai kẻ, trong lòng Tiêu Viêm không khỏi kinh ngạc, thì thầm một tiếng.', '2023-02-28 15:57:32', 13, 50, 5, ''),
	(31, 'Tiêu Viêm xuất hiện, đồng thời khiến Thôn Thiên Mãng và Tuyết Ma Thiên Viên chú ý. Một kẻ thì hưng phấn thè thè lưỡi với Tiêu Viêm, còn một kẻ thì phát ra một tiếng gào phẫn nộ xen lẫn chút bất an.', '2023-02-28 15:57:49', 13, 46, 4, ''),
	(32, '- Tiểu tử, đi thôi!\r\n\r\nKhông để ý đến Tuyết Ma Thiên Viên đang ngửa mặt rít gào, Tiêu Viêm hét to với Thôn Thiên Mãng. Hắn xoay đầu, vỗ mạnh đôi cánh, thân hình thẳng về phía sâu trong ngọn núi mà bay vút đi.', '2023-02-28 15:58:04', 14, 54, 3, ''),
	(33, 'Thôn Thiên Mãng thoáng chần chừ, lát sau, cái đuôi khổng lồ quẩy thật mạnh, thân hình vẫn đang bành trướng gấp rút thu nhò lại, hóa thành một đạo tiểu quang ảnh, bay nhanh theo hướng Tiêu Viêm', '2023-02-28 15:58:19', 14, 49, 5, ''),
	(34, 'Sau cùng, hai bóng dáng biến mất dần trong đêm tối, để lại Tuyết Ma Thiên Viên đang phẫn nộ đến phát điên nhưng không làm gì được bọn chúng.', '2023-02-28 15:58:31', 15, 53, 5, ''),
	(35, 'Trong đêm tối, hai bóng đen, một lớn một nhỏ bỗng nhiên từ trên trời hạ xuống trên một ngọn cây. Tiêu Viêm quay đầu, liếc mắt về sơn cốc đã khuất nơi cuối chân trời, nhẹ nhõm thở ra một hơi', '2023-02-28 15:58:34', 15, 54, 3, ''),
	(36, 'Xoay người lại, Tiêu Viêm nhìn Thôn Thiên Mãng trôi nổi trước mặt, hào quang bảy màu trên vảy của nó đã ảm đạm nhiều.', '2023-02-28 15:58:49', 16, 49, 5, ''),
	(37, 'Có lẽ lần này ác chiến với Tuyết Ma Thiên Viên khiến nó tiêu hao khá lớn. Bàn tay nhẹ nhàng vuốt ve chiếc đầu nho nhỏ của Thôn Thiên Mãng, Tiêu Viêm lấy một lọ Tử Tinh Nguyên từ trong nạp giới ra, cười nói:', '2023-02-28 15:58:56', 16, 48, 4, ''),
	(38, 'Đến đây, nhóc, tối nay làm thực không tệ, lần này ta cho ngươi ăn no.', '2023-02-28 16:00:33', 17, 48, 5, ''),
	(39, 'Nếu là trước đó, Thôn Thiên Mãng vừa thấy Tiêu Viêm lấy Tử Tinh Nguyên ra, sẽ lập tức búng người tới ngay. Nhưng lúc này, Thôn Thiên Mãng không nhục nhích, đứng giữa không trung một cách quỷ dị. Một mắt rắn yêu mị liếc Tiêu Viêm', '2023-02-28 16:01:25', 18, 49, 4, ''),
	(40, ' Lúc trong lòng hắn cảm thấy có gì đó khổng ổn thì một tiếng nói lạnh như băng bỗng nhiên từ miệng Thôn Thiên Mãng thốt ra:', '2023-02-28 16:01:29', 18, 55, 3, ''),
	(41, '- Ngươi thật đem bổn vương nuôi như một sủng vật sao?\r\n\r\nTiếng nói tuy lạnh như băng nhưng lại ẩn chưa một cỗ tà hỏa khiến lòng Tiêu Viêm tê dại.', '2023-02-28 16:01:45', 19, 55, 4, ''),
	(42, 'Khi cỗ tà hỏa từ bụng dưới vừa bốc lên, Tiêu Viêm như cảm giác được một trận hàn ý từ trong lòng trào ra', '2023-02-28 16:01:54', 19, 54, 5, ''),
	(43, ' Ánh mắt không rời khỏi Thôn Thiên Mãng trước mặt, hắn thấy sửng sốt trong nháy mắt, thân hình nhanh như điện giật lùi về phía sau, kinh hãi thốt lên:', '2023-02-28 16:01:58', 20, 46, 5, ''),
	(44, '- Mỹ Đỗ Toa nữ vương?\r\n\r\nTrong lúc Tiêu Viêm kinh sợ thoái lui, thân thể Thôn Thiên', '2023-02-28 16:02:00', 20, 54, 5, ''),
	(45, 'Mãng lại lần nữa bộc phát ra ánh hào quang bảy màu nhàn nhạt, thân hình từ từ uốn éo, lát sau, đã biến thành một vưu vật yêu mị, cơ hồ khiến cho mọi người đàn ông trong nháy mặt tình nguyện trở thành thú cưng.', '2023-02-28 16:02:03', 21, 49, 4, ''),
	(46, 'Dáng người thon dài, quần áo hở hang, chỉ vừa đủ che bộ ngực đầy đặn, hạ thân chỉ có vỏn vẹn một lớp da màu tím che phủ đến bắp đùi. ', '2023-02-28 16:02:05', 21, 54, 5, ''),
	(47, 'Dưới bộ váy bằng da lộ ra một đôi chân dài làm người khác miệng lưỡi khô khốc. Đường cong thon dài khiến đôi mắt kẻ khác mê muội. ', '2023-02-28 16:02:11', 22, 46, 5, ''),
	(48, 'Dời ánh mắt lên, cái eo rắn trắng như tuyết lại khiến trái tim Tiêu Viêm run lên liên hồi. Quả thật nàng là một vưu vật có thể khiến mọi người đàn ông điên cuồng.', '2023-02-28 16:02:13', 22, 54, 3, ''),
	(49, 'Nhưng, muốn thu phục vưu vật yêu mị này, nếu không có thực lực thông thiên, e rằng sẽ bị nàng nuốt luôn một phát vào bụng.\r\n', '2023-02-28 16:02:16', 23, 55, 5, ''),
	(50, 'Cho dù trong lòng biết rõ người đẹp trước là một xà mỹ nhân có thể ăn thịt người, nhưng Tiêu Viêm cũng không khỏi tránh khỏi có chút tâm tình nhộn nhạo. ', '2023-02-28 16:02:18', 23, 49, 4, ''),
	(51, 'Nhưng điểm tâm tình đó của hắn đã bị một ánh mắt lạnh như băng phóng tới làm cho tan thành mây khói trong nhảy mắt, chỉ còn lại một bụng phòng bị và cảnh giác.', '2023-02-28 16:02:22', 24, 48, 5, ''),
	(52, 'Ánh mặt cuối cùng dừng lại trên khuôn mặt không một chút tì vết, băng lãnh mà yêu mị, Tiêu Viêm cười khan một hơi. Hắn phát hiện tiếng cười của mình lại biến thành hơi khàn khàn.', '2023-02-28 16:02:23', 24, 49, 5, ''),
	(53, '- Nữ vương bệ hạ, thật trùng hợp, ta lại gặp mặt.\r\n\r\nMột câu nói cực kỳ thiểu năng đủ cho thấy trong lòng Tiêu Viêm đang khẩn trương đến độ nào. Đối với người đàn bà trước mặt này, hắn cảm thấy kiếng kị hơn cả đối với Vân Sơn của Vân Lam Tông. Nguồn: http://truyenfull.vn', '2023-02-28 16:02:26', 25, 55, 5, ''),
	(54, '- Cũng thật là trùng hợp đó. Nhưng cũng phải cảm ơn ngươi, nếu không phải ngươi không để Thôn Thiên Mãng và Tuyết Ma Thiên Viên đấu với nhau làm nó tiêu hao không ít năng lượng thì ta vẫn bị nó phong ấn.', '2023-02-28 16:02:30', 25, 48, 4, ''),
	(55, 'Mỹ Đỗ Toa Nữ Vương quét qua người Tiêu Viêm, khóe môi đỏ thắm hiện lên ý châm chọc.', '2023-02-28 16:02:36', 26, 49, 5, ''),
	(56, 'Khóe miệng há ra, Tiêu Viêm thật muốn tự cho mình một bạt tai. Không nghĩ tới kẻ đầu sỏ gây nên việc này lại là chính mình…', '2023-02-28 16:02:40', 26, 55, 3, ''),
	(57, '- Dược tài để luyện chế Dung Linh đan, người đã tập hợp đủ chưa?', '2023-02-28 16:02:46', 27, 55, 4, ''),
	(58, 'Không để ý đến sắc mặt khó coi của Tiêu Viêm, Mỹ Đỗ Toa Nữ Vương lạnh nhạt hỏi.', '2023-02-28 16:02:49', 27, 49, 5, ''),
	(59, 'Khóe mắt giật giật, trong lòng Tiêu Viêm chợt cười khổ. Tập hợp cái gì đây, hắn vốn đâu có coi chuyện này là quan trọng.', '2023-02-28 16:03:09', 28, 48, 4, ''),
	(60, 'Ngay cả việc của hắn còn lo chưa xong, làm gì có thời gian giúp nàng tìm dược tài Dung Linh Đan chứ. ', '2023-02-28 16:03:10', 28, 49, 5, ''),
	(61, 'Mà cho dù có thời gian, hắn câu giờ được thì sẽ tận lực câu, nếu không, người đàn bà này vừa có được dung linh đan, dung hợp xong linh hồn Thôn Thiên Mãng, thì kẻ xui xẻo đầu tiên chính là hắn. Đây là một người đàn bà đáng chết, từ lúc đầu đến giờ vẫn không từ bỏ sát tâm với hắn.', '2023-02-28 16:06:04', 29, 49, 3, ''),
	(62, 'Bất quá ngẫm lại cũng đúng. Với thân phận cao quý của Mỹ Đỗ Toa Nữ Vương, bậy giờ, vì đủ loại nhân tố đã trở thành sủng vật kiêm tay đánh thuê cho Tiêu Viêm, nàng cao ngạo như vậy, làm sao có thể nhẫn nhịn được? ', '2023-02-28 16:06:08', 29, 48, 5, ''),
	(63, 'Nếu không vì dung linh đan và linh hồn Thôn Thiên Mãng không ngừng phản phệ, nàng đã sớm dùng mấy chưởng xé Tiêu Viêm ra mảnh nhỏ.', '2023-02-28 16:06:14', 30, 55, 4, ''),
	(64, 'Nhìn bộ dáng im lặng không nói gì của Tiêu Viêm, trên mặt Mỹ Đỗ Toa Nữ Vương dần hiện lên một nụ cười lạnh và sát ý:', '2023-02-28 16:06:15', 30, 49, 5, ''),
	(65, 'Xem ra người vẫn chưa để tâm chuyện của ta. Như vậy, lưu lại ngươi còn có ích lợi gì?', '2023-02-28 16:06:21', 31, 55, 5, ''),
	(66, 'Dứt lời, Mỹ Đỗ Toa Nữ Vương vung mạnh bàn tay mềm mại của nàng. Theo sự huy động này, Tiêu Viêm kinh hãi phát hiện không gian xung quanh đều ngưng đọng lại. Mà thân thể hắn, như bị giam trong một cái lồng, không thể cử động.', '2023-02-28 16:06:23', 31, 49, 5, ''),
	(67, '"Đây là thực lực chân chính của Đấu Tông cường già sao?\r\n\r\nTùy ý giơ tay nhấc chân cũng có thể làm cho không gian đọng lại?" Trong lòng Tiêu Viêm hiện ra chút hoảng sợ. Nhân thức đối với Đấu Tông Cương Giả của hắn lại sâu thêm rất nhiều.', '2023-02-28 16:06:26', 32, 48, 3, ''),
	(68, 'Lạnh lùng nhìn Tiêu Viêm dùng sức giãy giụa, Mỹ Đỗ Toa Nữ Vương nhẹ nhàng tiến lên vài bước hướng về phía Tiêu Viêm.', '2023-02-28 16:06:28', 32, 55, 5, ''),
	(69, 'Bàn tay nhỏ nhắn dựng lên, hào quang bảy màu ngưng tụ trên lòng bàn tay, biến thành một thanh trường kiếm bảy màu thon dài và sắc bén.', '2023-02-28 16:06:31', 33, 49, 3, ''),
	(70, 'Tiêu Viêm dùng sức kháng cự một lúc, không gian chung quanh không hề rung động. Lúc Tiêu Viêm bất đắc dĩ muốn bỏ cuộc thì một cỗ linh hồn dao động cường hãn rốt cục từ trên hắc sắc giới chỉ tràn ra. ', '2023-02-28 16:06:33', 33, 55, 4, ''),
	(71, 'Mà dưới sự dao động linh hồn mạnh mẽ, không gian ngưng đọng xung quanh vỡ nát ầm ầm. Thân thể của Tiêu Viêm lúc này đã lấy lại được sự tự do.', '2023-02-28 16:06:36', 34, 49, 5, ''),
	(72, '- Ha ha, sơ giai Đấu Tông có thể làm không gian đọng lại. Không miệt thị Mỹ Đỗ Toa Nữ Vương ngươi, nhưng đệ tử của Dược Trần ta há có thể để người nói giết là giết ngay sao?', '2023-02-28 16:06:38', 34, 55, 5, ''),
	(73, 'Thân ảnh hư áo của Dược Lão trong hắc sắc giới chỉ đã bốc lên, trôi nổi bên cạnh Tiên Viêm, thản nhiên nhìn chăm chú vào Mỹ Đỗ Toa Nữ Vương ở đối diện.', '2023-02-28 16:06:41', 35, 49, 5, ''),
	(74, 'Lạnh như băng nhìn Dược Lão đột nhiên xuất hiện. Mỹ Đồ Toa Nữ Vương trên gương mặt đẹp đẽ lại không chút nào kinh ngạc, cười lạnh nói:', '2023-02-28 16:06:43', 35, 48, 5, ''),
	(75, '- Đã sớm cảm ứng được tên gia hỏa này bên người có cường giả ẩn nấp, hóa ra là linh hồn thể.\r\n\r\nTrong miệng mặc dù nói như vậy, nhưng Mỹ Đồ Toa Nữ Vương cũng chậm rãi thu hồi cước bộ, trước mặt thân hình lão giả hư ảo này, mơ hồ cho nàng cảm giác áp bách bên trong, so với Vân Sơn lúc trước cũng không yếu hơn, nếu là nàng đi vào trạng thái đỉnh. ', '2023-02-28 16:06:46', 36, 46, 5, ''),
	(76, 'Cũng là không sợ, nhưng thân thể lúc này, lại phải lúc nào cũng phân ra tâm thần đến áp chế linh hồn Thôn Thiên Mãng. Từ sự phân thần đó. Thực lực cũng bị chiết khấu một ít.', '2023-02-28 16:06:48', 36, 49, 5, ''),
	(77, 'Dược Lão nhàn nhạt cười cười. Phất tay làm cho Tiêu Viêm lui ra phía sau một ít, rồi mới cười nói:\r\n\r\n- Ta cũng đã rõ ràng tình cảnh hiện tại của ngươi, cho nên ngươi cũng không cần ở trước mặt ta sát khí nghiêm nghị như vậy, mặc dù hiện giờ ta chỉ là linh hồn trạng thái, thực lực tổn thất lớn, nhưng nếu thật sự phải quyết đấu, chỉ sợ ngươi không có được tiện nghi gì đâu.', '2023-02-28 16:06:52', 37, 48, 3, ''),
	(78, 'Mỹ Đồ Toa Nữ Vương cười lạnh một tiếng, nhưng trong tay lại không có động tác gì. Hiển nhiên Dược Lão xuất hiện, cũng cho nàng kinh sợ khá lớn.', '2023-02-28 16:06:54', 37, 46, 5, ''),
	(79, '- Ta cũng không nghĩ cùng ngươi có cái gì xung đột. Chỉ là đệ tử của ta, cũng còn không tới phiên ngươi tùy ý chém giết, ngươi nếu đợi ở nơi này cảm thấy khó chịu thì có thể trực tiếp rời đi, không ai ngăn trở ngươi.', '2023-02-28 16:06:58', 38, 48, 5, ''),
	(80, 'Dược Lão bình thản cười nói. Đồng dạng là có phong duệ sắc bén, lúc hắn tung hoành đại lục, đừng nói Đấu Tông, cho dù là Đấu Tôn cường giả đều phải đối với hắn khách khí ba phần, cái gì mà Mỹ Đồ Toa nữ vương có lẽ có thể làm cho người khác kiêng kị, nhưng đối với hắn mà nói, lại là không chút uy hiếp nào.', '2023-02-28 16:07:08', 38, 46, 4, ''),
	(81, 'Nghe vậy, Mỹ Đồ Toa Nữ Vương nhíu mày. Biết giờ Thôn Thiên Mãng cơ hồ có dấu hiệu nhận Tiêu Viêm làm chủ, mà nàng lại không có hoàn toàn lấy được quyền khống chế thân thể. Đi cùng không đi, còn không tới phiên nàng để làm chủ. Còn nữa, dung linh đan phương thuốc kia còn đang trong tay Tiêu Viêm, nàng còn muốn đem loại này đan dược luyện chế ra.', '2023-02-28 16:10:31', 39, 48, 5, ''),
	(82, 'Còn phải dựa vào lực lượng của hắn, cho nên nàng như thế nào lại rời đi?\r\n\r\n- Ta cùng với hắn từng có ước định, nếu là ta bảo vệ hắn đang trong tay Vân Sơn bình yên vô sự, hắn liền luyện chế linh đan cho ta, nhưng hôm nay đã qua gần một năm thời gian, thế nhưng hắn lại đã đem ước định kia quên đi không còn một mảnh, người này không thủ tín, lưu lại để làm gì?', '2023-02-28 16:10:35', 39, 46, 3, ''),
	(83, 'Mỹ Đồ Toa Nữ Vương lạnh lùng liếc Tiêu Viêm liếc mắt một cái nói.\r\n\r\nHiện giờ có Dược Lão hiện thân, Tiêu Viêm trong lòng khẩn trương ngược lại dần dần tiêu giảm rất nhiều.', '2023-02-28 16:10:39', 40, 48, 4, ''),
	(84, 'Cho nên nhìn thấy Mỹ Đồ Toa Nữ Vương kia ánh mắt lạnh như băng, hắn bất đắc dĩ vuốt tay "đại tỷ, ngươi thật đúng là khinh ta là ngốc tử, càng sớm luyện ra dung linh đan, chẳng phải là bị chết càng nhanh sao?"', '2023-02-28 16:10:42', 40, 49, 5, ''),
	(85, '- Ha ha, nếu không phải ngươi lòng mang ý giết chóc. Đệ tử ta cũng là cực kỳ vui mừng tìm kiếm dược liệu cho ngươi. Sự tình thành như vậy, thật cũng không thể toàn bộ trách hắn.', '2023-02-28 16:10:45', 41, 46, 5, ''),
	(86, 'Dược Lão cười nói, hắn tự nhiên là rõ ràng sự tình từ đầu đến cuối.\r\n\r\nMỹ Đồ Toa Nữ Vương hừ lạnh một tiếng, cũng là vẫn chưa biện bác, nàng lúc trước đích thật là có ý niệm chỉ cần Tiêu Viêm một khi luyện chế ra Dung Linh đan liền một chưởng đánh chết hắn.', '2023-02-28 16:10:48', 41, 48, 5, ''),
	(87, '- Ha ha, Mỹ Đồ Toa, ta cũng không cùng ngươi tốn nước miếng nhiều, ngươi thật đúng là rất cần dung linh đan kia?\r\n\r\nNhìn thấy ánh mắt Mỹ Đồ Toa Nữ Vương đối diện lóe ra, Dược Lão suy nghĩ một chút, bỗng nhiên nói.', '2023-02-28 16:10:51', 42, 48, 5, ''),
	(88, '- Lời này của Ngươi không khỏi hỏi có chút dư thừa.\r\n\r\n- Một khi đã như vậy, chúng ta song phương có qua có lại, bất quá điều kiện tiên quyết là trước tiên đem mấy cái ý niệm trong lòng kia xóa đi. Thôn Thiên Mãng đi theo bên người Tiêu Viêm việc này cũng là không trách ai được, huống chi cho dù nó nhận Tiêu Viêm làm chủ, cũng không liên quan ngươi chuyện gì. Người bên ngoài cũng không thay đổi được.', '2023-02-28 16:10:51', 42, 49, 4, ''),
	(89, 'Dược Lão cười cười nói:\r\n\r\n- Nếu là ngươi có thể vứt bỏ sát ý trong lòng. Ta có thể cùng ngươi cam đoan, trong vòng một năm, đem dung linh đan hoàn thành bày ra trước mặt ngươi, nhưng là, đổi lại, ngươi phải ở đây bảo vệ đệ tử ta trong vòng một năm, không cần lo lắng về tính mạng, như thế nào?', '2023-02-28 16:10:57', 43, 46, 5, ''),
	(90, '- Ước định của ta cùng với hắn, đã sớm hoàn thành tốt. Lúc trước ta đã bảo vệ hắn trong tay Vân Sơn chạy thoát, nếu không bằng thực lực của hắn, còn muốn cùng Vân Sơn khiêu chiến? Nếu ta đã hoàn thành xong ước định, như vậy hắn nên lập tức luyện chế dung linh đan cho ta, hiện giờ dựa vào cái gì còn muốn lại cùng ngươi ước hẹn tiếp?', '2023-02-28 16:10:59', 43, 49, 4, ''),
	(91, 'Mỹ Đồ Toa Nữ Vương thanh âm bên trong mơ hồ có một tia tức giận.\r\n\r\n- Lần đó ước định. Song phương đều có nơi trái với quy định. Tự nhiên là không nên tính.', '2023-02-28 16:11:02', 44, 46, 5, ''),
	(92, 'Dược Lão đáp\r\n\r\n- Muốn ta làm hộ vệ cho hắn một năm thời gian. Mơ tưởng!\r\n\r\nMỹ Đồ Toa Nữ Vương âm thanh lạnh lùng nói, không chút do dự cự tuyệt.', '2023-02-28 16:11:05', 44, 55, 4, ''),
	(93, '- Nói như vậy, ngươi liền đi tìm người có thể luyện chế Dung Linh đan như hắn đi.\r\n\r\nDược Lão nhướng mày, nói.\r\n\r\n- Vậy đem phương thuốc Dung Linh đan cho ta!', '2023-02-28 16:11:08', 45, 51, 3, ''),
	(94, '- Ha ha, cái này không thể được, Dung Linh đan phương thuốc này là Tiêu Viêm hao hết tâm cơ mới thông qua Luyện Dược Sư đại hội có được, như thế nào có thể cho ngươi?\r\n\r\nDược Lão cười giễu cợt, lắc đầu nói.', '2023-02-28 16:11:11', 45, 55, 5, ''),
	(95, 'Hít sâu một hơi, Mỹ Đồ Toa Nữ Vương sắc mặt cũng dần dần âm trầm xuống, nàng vốn là không phải người thích tốn nước miếng, nếu không có kiêng kị thực lực của Dược Lão, chỉ sợ đương trường liền trực tiếp động thủ, nhưng hôm nay Dược Lão lần này ngoan cố, lại làm nàng trong lòng lại lần nữa sát ý bắt đầu khởi động.', '2023-02-28 16:11:14', 46, 51, 5, ''),
	(96, 'Theo Mỹ Đồ Toa Nữ Vương sắc mặt âm trầm, một cỗ khí thế bàng bạc làm cho người ta hoảng sợ chậm rãi từ trong cơ thể nàng trào ra. Mà trông từ xa cỗ khí thế cường hãn này, không khí quanh mình, tựa hồ vào bất cứ thời khắc nào cũng có thể phóng ra.', '2023-02-28 16:11:16', 46, 55, 3, ''),
	(97, 'Vạn vật yên tĩnh, đây là bá đạo cùng cường hãn của Đấu Tông cường giả.\r\n\r\nNhưng mà lúc này cỗ khí thế bàng bạc áp bách xuống. Dược Lão thân hình cũng không nhúc nhích, chỉ là phía trên khuôn mặt bình thản già nua kia, cũng là dần dần lộ ra chút lạnh lẽo, vô hình linh hồn năng lượng kéo dài mà ra.', '2023-02-28 16:11:19', 47, 51, 5, ''),
	(98, 'Nhanh chóng bao phủ nửa bên bầu trời. Mặc dù thân là linh hồn trạng thái cũng không thể vận dụng đấu khí, nhưng nguyên nhân là Luyện Dược Sư, linh hồn Dược Lão cũng là cường đại dị thường, hơn nữa có Cốt Linh Lãnh Hỏa hiệp trợ, thậm chí có thể chỉ bằng vào linh hồn lực lượng có thể đủ cùng người đấu, đương nhiên, chỉ là sử dụng linh hồn lực lượng, tự nhiên là không đạt được trạng thái đỉnh của hắn, nhưng là dùng để đối phó Mỹ Đồ Toa Nữ Vương trước mặt cũng không đạt trạng thái đỉnh thật ra không có vấn đề lớn.', '2023-02-28 16:11:22', 47, 55, 3, ''),
	(99, 'Hai cỗ khí thế đồng dạng cường hãn khủng bố tại ngọn núi phía trên dần dần dũng mãnh tràn tới, dưới hai cỗ khí thế áp bách mặt đất run nhè nhẹ, sau một lúc lâu, dĩ nhiên là trong ánh mắt khiếp sợ của Tiêu Viêm dần dần nứt ra từng khe lớn nhỏ.\r\n', '2023-02-28 16:11:25', 48, 51, 3, ''),
	(100, 'Theo thời gian trôi qua, hai cỗ khí thế càng ngày càng mạnh, nhưng mà đang lúc Mỹ Đồ Toa Nữ Vương nhịn không được muốn ra tay, đột nhiên sắc mặt của nàng cũng mạnh mẽ biến đổi, khí thế căng thẳng kia, cũng là lúc này sụp đổ tan tành, bàn tay mềm ôm chặt cái trán, sắc mặt cấp tốc biến đổi. Thất thải quang mang cũng không ngừng từ trong cơ thể trào ra.', '2023-02-28 16:11:27', 48, 55, 5, ''),
	(101, '- Chết tiệt!\r\n\r\nDùng sức cắn môi. Mỹ Đồ Toa Nữ Vương hung hăng mắng một tiếng, trong cơ thể Thôn Thiên Mãng dĩ nhiên là dưới loại tình huống này đi ra tranh đoạt quyền khống chế thân thể, hơn nữa lần này còn phản kháng kịch liệt, làm hại nàng không thể không vội vàng ngưng tụ lực lượng áp chế. Mà nếu như vậy, còn như thế nào có thể cùng Dược Lão tranh đấu.', '2023-02-28 16:14:25', 49, 55, 4, ''),
	(102, 'Nhìn thấy trạng thái Mỹ Đồ Toa Nữ Vương như vậy. Tiêu Viêm cũng hiểu cái gì, lập tức trong lòng thở dài nhẹ nhõm một hơi, nữ nhân này thực lực quá mức khủng bố, xem như Dược Lão có thể đủ thắng nàng, chỉ sợ cuối cùng cũng sẽ bởi vì linh hồn lực lượng hao hết mà lâm vào ngủ say. ', '2023-02-28 16:14:27', 49, 51, 5, ''),
	(103, ' Tuy nói hiện giờ Tiêu Viêm đã không còn giống như trước đây dựa vào Dược Lão, nhưng là bất kể như thế nào, có Dược Lão kinh nghiệm phong phú, mình có thể bỏ bớt đi không ít phiền toái.', '2023-02-28 16:14:30', 50, 55, 5, ''),
	(104, 'Thất thải quang mang càng ngày càng thịnh, cuối cùng, con ngươi của Mỹ Đồ Toa Nữ Vương trở nên thoáng có chút mờ mịt.\r\n\r\nHít sâu một hơi, nỗ lực áp chế Thôn Thiên Mãng phản kích, Mỹ Đồ Toa Nữ Vương lạnh lùng nhìn Dược Lão đối diện, không cam lòng nói:', '2023-02-28 16:14:33', 50, 51, 3, ''),
	(105, '- Hảo, theo ý ngươi, trong vòng một năm, nếu ngươi có thể cho ta dung linh đan. Ta đây liền bảo vệ hắn một năm, nhưng nếu là các ngươi lại tiếp tục đùa bỡn ta, vậy cho dù liều mạng bị Thôn Thiên Mãng phản phệ bổn vương cũng muốn cho các ngươi tổn thất thảm trọng!', '2023-02-28 16:14:37', 51, 55, 5, ''),
	(106, '- Ha ha, như vậy tự nhiên tốt nhất, trong vòng một năm, dung linh đan chắc chắn dâng. Chỉ bất quá lão phu cũng đã hy vọng nữ vương bệ hạ cũng không nên giống như lần trước, lòng mang sát ý nặng nề. Nếu không nói, liều mạng cho dù bị lâm vào ngủ say, lão phu cũng phải thiêu hủy thân thể ngươi!', '2023-02-28 16:14:39', 51, 51, 5, ''),
	(107, 'Dược Lão cười nhẹ một tiếng, trong tiếng cười quả thật có vô cùng uy hiếp ý, mà giống như là vì trợ uy, trong lúc dứt lời. Sâm bạch sắc hỏa diễm mạnh mẽ trong cơ thể bạo dũng ra, đem bao bọc cơ thể vào trong.', '2023-02-28 16:14:42', 52, 55, 5, ''),
	(108, 'Bạch Sắc Hỏa Diễm, từ từ bốc lên. Song mặc dù hỏa diễm bốc lên lợi hại, nhưng nhìn qua lại thủy chung làm cho người ta một loại cảm giác lạnh như băng kỳ dị.\r\n\r\n- Dị Hỏa?', '2023-02-28 16:14:46', 52, 51, 3, ''),
	(109, 'Nhìn thấy Bạch Sắc Hỏa Diễm kia, Mỹ Đồ Toa Nữ Vương hai má hơi đổi, ánh mắt biến hóa không ít, từng nếm qua Thanh Liên Địa Tâm Hỏa, tự nhiên là rõ ràng ngọn lửa này khủng bố ra sao, tuy nói Bạch Sắc Hỏa Diễm trước mặt này cũng không giống Thanh Liên Địa Tâm Hỏa ở mặt ngoài nóng cháy, thế nhưng cảm giác quỷ dị này, liền làm cho Mỹ Đồ Toa Nữ Vương không dám khinh thường.', '2023-02-28 16:14:49', 53, 55, 5, ''),
	(110, '- Chỉ cần các ngươi đừng phạm quy nữa, bổn vương tự nhiên lại tuân thủ lời nói này, ta hy vọng lần sau khi tỉnh lại. Có thể nhìn thấy chân chính Dung Linh đan.', '2023-02-28 16:14:51', 53, 51, 5, ''),
	(111, 'Nhàn nhạt nói một câu, thất thải quang mang trong cơ thể Mỹ Đồ Toa Nữ Vương đột nhiên bùng nổ. thân thể lại lần nữa mấp máy. Một lát sau, một cái đẹp đẻ quyến rũ tuyệt sắc vưu vật kia, lại lần nữa biến trở về thất thải tiểu xà dài hơn thước.', '2023-02-28 16:14:55', 54, 55, 4, ''),
	(112, 'Biến trở về hình Thôn Thiên Mãng, hơi hơi lắc lư cái đuôi, nhanh như chớp xuất hiện bên cạnh Tiêu Viêm. Hướng về phía hắn kêu bằng ngôn ngũ loài rắn tê tê, bên trong vẻ đẹp đẽ kia, giống như là có thêm một chút nhân tính hóa thân thiết.', '2023-02-28 16:14:57', 54, 46, 3, ''),
	(113, '- Ha ha, ta không sao.\r\n\r\nÔn nhu vuốt ve Thôn Thiên Mãng, Tiêu Viêm cười khổ nói:\r\n\r\n- Tiểu tử kia. Nhờ vào ngươi đấy, bằng không lần này sợ là không thể thiếu một hồi ác chiến.', '2023-02-28 16:15:00', 55, 45, 5, ''),
	(114, '- Bất quá ít nhất cùng nàng đem nói ra hết. Ngày sau cũng không tiếp tục lo lắng nàng có thể đột nhiên hạ sát với ta. Mỹ Đồ Toa Nữ Vương mặc dù lạnh lùng vô tình, nhưng lại ngạo khí mười phần. Hẳn là sẽ không đổi ý.', '2023-02-28 16:15:02', 55, 46, 5, ''),
	(115, 'Dược Lão quay đầu lại hướng về phía Tiêu Viêm cười nói.\r\n\r\n- Hy vọng như thế đi.\r\n\r\nTiêu Viêm khẽ gật đầu, bàn tay vừa lật. Đem bình Địa Tâm Thối Thể Nhũ thật lấy ra, nhẹ giọng nói:', '2023-02-28 16:15:05', 56, 45, 5, ''),
	(116, 'Bên trong sơn động rộng rãi sáng ngời, Tiêu Viêm nhìn trước mặt bày ra một cái chậu gỗ lớn, chậu gỗ bên trong đựng đầy nước trong suốt, ở một bên chậu gỗ, có một cái giá gỗ giản dị, bày đầy đủ loại dược liệu, nhìn qua nhiều khủng bố cỡ chừng mấy chục loại, để tìm kiếm chừng đó dược liệu, Tiêu Viêm ước chừng tiêu phí thời gian gần ba ngày.', '2023-02-28 16:15:08', 56, 46, 5, ''),
	(117, '- Đưa Địa Tâm Thối Thể Nhũ cho ta.\r\n\r\nSau khi kiểm tra dược liệu cần thiết có đầy đủ hết hay chưa, Dược Lão nhìn Tiêu Viêm nói.', '2023-02-28 16:15:11', 57, 45, 3, ''),
	(118, 'Nghe vậy, Tiêu Viêm vội vàng theo lời đem các loại đồ vật bỏ vào trong tay Dược Lão,\r\n\r\nSau khi cầm bình ngọc đựng Địa Tâm Thối Thể Nhũ, hơi hơi suy nghĩ một chút. Chợt mở nắp bình, nhất thời một cỗ sương màu xanh biếc nhàn nhạt liền phiêu đãng bay ra, cuối cùng ngưng tụ tại miệng bình. Thật lâu không tiêu tan', '2023-02-28 16:15:17', 57, 46, 5, ''),
	(119, 'Dược Lão hít sâu lấy một ngụm sương mù, liền cảm nhận được một cỗ khí tức ấm áp nhẹ nhàng chảy xuôi qua bên trong linh hồn, không khỏi cười thở dài:', '2023-02-28 16:15:27', 58, 45, 4, ''),
	(120, '- Không hổ là linh vật ngưng tụ từ lực lượng của đại địa, dĩ nhiên tinh thuần nồng đậm như thế, khó trách có công hiệu tẩy tủy luyện cốt như vậy.', '2023-02-28 16:15:28', 59, 46, 5, ''),
	(121, 'Sau tiếng cười tán thưởng, Dược Lão đem bình ngọc hơi hơi nghiêng, thật cẩn thận đổ mười giọt chất lỏng giống như phỉ thúy ngọc châu vào bên trong chậu gỗ. Sau khi mười giọt phỉ thúy rời bình, trong bình chất lỏng liền giảm bớt gần một phần tư.', '2023-02-28 16:17:47', 59, 45, 3, ''),
	(122, 'Mà sau khi mười giọt Địa Tâm Thối Thể Nhũ nhỏ vào, chỉ thấy dịch thủy trong suốt bên trong chậu gỗ, lấy tốc độ mắt thường có thể thấy được chuyển hóa thành màu nồng đậm phỉ thúy, hơn nữa, ở phía trên mặt nước còn dần dần thẩm thấu ra một cỗ sương mù nhàn nhạt. Kéo dài không tiêu tan, nhìn qua cực kỳ kỳ dị.', '2023-02-28 16:18:01', 60, 46, 5, ''),
	(123, 'Tò mò nhìn biến hóa của thủy dịch bên trong chậu gỗ. Tiêu Viêm lắc lắc đầu, ngạc nhiên nói:\r\n\r\n- Chẳng lẽ lần này cần ở bên trong này tu luyện?\r\n\r\n- Ân.', '2023-02-28 16:18:04', 60, 45, 4, ''),
	(124, 'Dược Lão hết sức chăm chú nhìn nhan sắc biến hóa của thủy dịch, thuận miệng nói:\r\n\r\n- Năng lượng của Địa Tâm Thối Thể Nhũ quá mức khổng lồ, bằng thực lực ngươi bây giờ căn bản không có khả năng trực tiếp uống vào, cho nên chỉ có thể áp dụng phương thức này, hơn nữa cho dù là như vậy.', '2023-02-28 16:18:06', 61, 46, 4, ''),
	(125, ' cũng đã phải phối hợp dược vật khác đi cùng, phương pháp mới này có thể phát ra công hiệu tẩy tủy luyện cốt, bằng không nếu là mạnh mẽ mà nói, đừng nói tẩy tủy, chỉ sợ ngay cả cái mạng nhỏ kia của ngươi đều đã trực tiếp bị rửa đi.', '2023-02-28 16:18:09', 61, 45, 5, ''),
	(126, 'Tiêu Viêm ngượng ngùng cười, thể cốt chính mình không yếu như vậy chứ?\r\n\r\n- Bỏ thêm Hương Chúc Thảo cho ta.', '2023-02-28 16:18:12', 62, 56, 3, ''),
	(127, 'Không để ý đến Tiêu Viêm âm thầm nói thầm, Dược Lão vẫn quan sát một chút màu sắc cùng nồng độ thủy dịch, thản nhiên phân phó.', '2023-02-28 16:18:18', 62, 46, 5, ''),
	(128, 'Nghe vậy, Tiêu Viêm bàn tay rất nhanh với lên trên giá gỗ, cầm lấy một gốc cây hình dạng cỏ nhỏ màu đỏ mùi như hương chúc, sau đó đem nó đưa cho Dược Lão..\r\n\r\nCầm gốc cây Hương Chúc Thảo, bàn tay Dược Lão nhoáng lên một cái, một đoàn sâm bạch hỏa diễm liền từ lòng bàn tay thành hình, sâm bạch hỏa diễm lượn lờ bốc lên, giống như một đoàn băng diễm.', '2023-02-28 16:18:23', 63, 45, 4, ''),
	(129, 'Tiện tay đem gốc cây vào bên trong ngọn lửa, chỉ chốc lát thời gian, Hương Chúc Thảo liền nhanh chóng héo rũ, cuối cùng bốc cháy bên trong Cốt Linh Lãnh Hỏa, biến thành một giọt chất lỏng màu đỏ.', '2023-02-28 16:18:28', 63, 46, 5, ''),
	(130, 'Sử dụng Cốt Linh Lãnh Hỏa đem giọt chất lỏng màu đỏ luyện hóa một lúc sau, Dược Lão bấm tay bắn ra, chất lỏng màu đỏ liền lọt vào bên trong chậu gỗ.', '2023-02-28 16:18:41', 64, 45, 5, ''),
	(131, 'Khi giọt chất lỏng màu đỏ lọt vào, mặt nước vốn giống như Phỉ Thúy, cũng lặng yên biến thành một ít đỏ sậm.\r\n\r\n- Thanh Liên Quả', '2023-02-28 16:18:47', 64, 56, 3, ''),
	(132, 'Dược Lão lại lần nữa nhàn nhạt mở miệng, Tiêu Viêm vội vàng từ giá gỗ tìm kiếm ra gốc cây quả dược liệu.', '2023-02-28 16:18:53', 65, 46, 4, ''),
	(133, 'Trải qua mấy phút luyện hóa, Thanh Liên Quả cũng biến thành một giọt chất lỏng màu xanh rơi vào bên trong chậu gỗ, theo đó thủy dịch cũng nhiều thêm một chút màu xanh nhàn nhạt.', '2023-02-28 16:19:03', 65, 45, 5, ''),
	(134, '- Xà Thoát Hoa.\r\n\r\n- Phật Diễm Căn.\r\n\r\nMột loạt tên gốc cây dược liệu bất đồng không ngừng từ trong miệng Dược Lão phát ra, mà theo những thanh âm này phun ra, dược liệu phía trên giá gỗ kia cũng là cấp tốc giảm bớt, 1h sau, mấy chục loại dược liệu, tất nhiên đã hoàn toàn biến thành chất lỏng màu sắc không giống nhau bị đầu nhập vào bên trong chậu gỗ.', '2023-02-28 16:19:07', 66, 56, 5, ''),
	(135, 'Sau khi đem loại dược liệu cuối cùng luyện hóa, Dược Lão cũng khẽ thở dài nhẹ nhõm một hơi, ánh mắt nhìn phía trong chậu gỗ, nhìn thấy mặt nước trở thành có ngũ sắc sặc sỡ, lúc này mới thỏa mản gật gật đầu, quay đầu sang Tiêu Viêm ở bên nói', '2023-02-28 16:19:14', 66, 46, 4, ''),
	(136, '- Cởi quần áo ra, đi vào trong đó tu luyện, cho đến khi màu sắc mặt nước lại lần nữa biến trở về nước trong mới thôi.\r\n\r\nTiêu Viêm kinh ngạc nhìn chỉnh hợp mấy chục loại dược liệu tinh túy mà hội tụ thành chất lỏng Ngũ sắc', '2023-02-28 16:19:24', 67, 56, 5, ''),
	(137, 'Trong lòng không khỏi cảm thấy bội phục thủ đoạn cô đọng dược liệu vô cùng kì diệu của Dược Lão, thủ đoạn như vậy, cùng với thiên phú không quan hệ, tất cả đều cần phải tích lũy kinh nghiệm hiểu biết đủ loại phương thuốc dược liệu mới có thể đạt tới.', '2023-02-28 16:19:28', 67, 45, 5, ''),
	(138, '- Sau khi tiến vào cẩn thận một chút, mặc dù Địa Tâm Thối Thể Nhũ trải qua dược liệu luyện hoá trung hoà, hiệu lực trở nên ôn thuận hơn rất nhiều, nhưng lại ẩn chứa năng lượng không giảm mà còn tăng, cho nên trong khi tu luyện ở đây, có lẽ có một chút đau đớn, vượt qua được sẽ làm ngươi được ích lợi không nhỏ.', '2023-02-28 16:19:37', 68, 49, 5, ''),
	(139, 'Dược Lão vỗ vỗ đầu, cười nói. Nguồn truyện: Truyện FULL\r\n\r\nTiêu Viêm gật gật đầu, rất nhanh đem quần áo trên người bỏ đi, sau đó có chút vội vàng xoay người kích động tiến lên bên trong chậu gỗ.', '2023-02-28 16:19:41', 68, 56, 5, ''),
	(140, 'Khi thân thể xâm nhập vào mặt nước Ngũ Sắc sặc sỡ, Tiêu Viêm thân thể nhất thời cảm thấy lạnh run, nước này độ ấm thấp đủ dọa người. Nếu không sử dụng đấu khí hộ thể, thậm chí ngay cả da tay cũng cảm thấy một trận đau đớn nhập vào tận tim.', '2023-02-28 16:19:52', 69, 49, 5, ''),
	(141, '- Không cần thi triển đấu khí bao bọc thân thể, sẽ làm trở ngại dược lực đi vào trong cơ thể, nước này lạnh như băng là bởi vì nguyên nhân Cốt Linh Lãnh Hỏa của ta, đối với ngươi không có gì xấu.', '2023-02-28 16:19:54', 69, 56, 5, ''),
	(142, 'Ngay khi Tiêu Viêm nhịn không được muốn sử dụng đấu khí chống lại cái lạnh, thanh âm Dược Lão bỗng nhiên vang lên.', '2023-02-28 16:22:22', 70, 56, 4, ''),
	(143, 'Nghe vậy, Tiêu Viêm cũng đành phải bất đắc dĩ gật đầu, thân thể ngâm ở bên trong chất lỏng Ngũ sắc, hắn có thể cảm giác được rõ ràng trong đó ẩn chứa năng lượng khổng lồ, lúc này còn chưa đi vào trạng thái tu luyện, mà trong nước tràn đầy năng lượng bắt đầu nhịn không được hướng trong cơ thể chui vào, làm cho Tiêu Viêm cả người có cảm giác ngứa ngáy, giống như có con kiến đang ở trên thân thể di động qua lại.', '2023-02-28 16:22:24', 70, 45, 3, ''),
	(144, 'Lắc mạnh đầu, Tiêu Viêm đè nén cảm giác khó chịu trên thân thể xuống, mấy năm nay hắn nếm qua không ít đau khổ, bởi vậy cảm giác tê dại này đối với hắn cũng đã không tạo thành nhiều trở ngại, ngồi xếp bằng ở bên trong chậu gỗ, hai tay kết xuất thủ ấn tu luyện, đôi mắt dần dần nhắm lại', '2023-02-28 16:22:33', 71, 56, 5, ''),
	(145, ', hô hấp lặng yên trở nên vừa chậm, vừa sâu. Nửa ngày sau, trên thân thể cảm giác ngứa ngáy liền chậm rãi giảm đi, mà Tiêu Viêm, cũng tiến nhập vào trong trạng thái tu luyện.', '2023-02-28 16:22:37', 71, 45, 5, ''),
	(146, 'Cạnh chậu gỗ, Dược Lão nhìn Tiêu Viêm đi vào trạng thái tu luyện, mỉm cười gật gật đầu, nhẹ giọng nói:', '2023-02-28 16:22:44', 72, 56, 5, ''),
	(147, '- Tẩy tủy luyện cốt tất nhiên có chỗ tốt, nhưng trong đó đau khổ cũng không nhỏ, vượt qua được là đại lộ bằng phẳng, không qua được, không chỉ thất bại trong gang tấc, kinh mạch còn có thể bị cỗ năng lượng', '2023-02-28 16:22:48', 72, 56, 3, ''),
	(148, ' khổng lồ kia đánh trả, kết quả là cái mất nhiều hơn cái được, tuy nói có chút nguy hiểm, song con đường tu luyện, không có tinh thần mạo hiểm, như thế nào có thể đi xa?\r\n', '2023-02-28 16:23:09', 73, 45, 5, ''),
	(149, 'Khi Tiêu Viêm đi vào trạng thái tu luyện, mặt nước bên trong chậu gỗ cũng đột nhiên nổi lên một đám bọt nước thật nhỏ, một lát sau, bọt nước bắt đầu khởi động càng thêm kịch liệt, trông giống như ở nước sôi, tuy rằng trông như nước sôi nhưng vẫn như cũ làm cho Tiêu Viêm vô cùng lạnh lẽo.', '2023-02-28 16:23:11', 73, 49, 3, ''),
	(150, 'Khi bọt nước dâng lên chốc lát, thân thể Tiêu Viêm cũng là run lên kịch liệt, hắn có thể cảm giác được, lúc này vô số cỗ năng lượng tinh thuần bên t', '2023-02-28 16:23:15', 74, 56, 5, ''),
	(151, 'trong thủy dịch giống như đã bị một lực lượng nào đó dẫn dắt, theo lỗ chân lông toàn thân mở ra, mạnh mẽ đi vào trong cơ thể!', '2023-02-28 16:23:19', 74, 45, 4, ''),
	(152, 'Bởi vì năng lượng tràn vào thật sự là quá mức khổng lồ, Tiêu Viêm thậm chí nhận thấy được có chút trướng đau lên, nhưng mà cỗ năng lượng tinh thuần đó vẫn chưa bởi vì cảm thụ của hắn mà có điều đình trệ,', '2023-02-28 16:23:26', 75, 56, 5, ''),
	(153, ' ngược lại tốc độ càng thêm hung mãnh chảy vào, tới cuối cùng, một cỗ tinh thuần năng lượng bởi vì không thể chui vào tiếp được nên đã ở dưới làn da Tiêu Viêm lung tung chuyển động. Mà theo động đậy của chúng nó, chung quanh làn da trên thân thể Tiêu Viêm cũng tạo thành từng đường dấu vết tán loạn, bộ dáng kia giống như dưới làn da đó cất dấu một con rắn nhỏ. Nhìn qua có chút đáng sợ.', '2023-02-28 16:23:29', 75, 45, 3, ''),
	(154, 'Bề ngoài dữ tợn khủng bố song Tiêu Viêm tự nhiên là không phát hiện ra, hắn hiện tại chỉ có thể toàn lực vận chuyển tâm thần, tùy thời chú ý động tĩnh trong cơ thể.', '2023-02-28 16:23:34', 76, 56, 4, ''),
	(155, 'Năng lượng Ngũ sắc tràn vào trong cơ thể, vẫn chưa nghe Tiêu Viêm chỉ huy, mà là cực kỳ có mục tiêu tính thẳng hướng các nơi cốt cách trong cơ thể', '2023-02-28 16:23:38', 76, 45, 5, ''),
	(156, 'Mà phàm là bọn chúng gặp được cốt cách, đều là trong nháy mắt biến ảo thành Ngũ sắc, mà tại lúc này, Tiêu Viêm có thể mơ hồ cảm giác được, có cái gì đó xâm nhập vào cốt cách bên trong, hơn nữa còn cùng với bên trong cốt tủy hỗn tạp lại với nhau.', '2023-02-28 16:23:40', 77, 56, 5, ''),
	(157, 'Đối với hành động đó của năng lượng, Tiêu Viêm không biện pháp nào ngăn cản, cho nên, hắn ngoại trừ ở ngoài trơ mắt nhìn trong cơ thể càng ngày càng nhiều cốt cách bị nhuộm đẫm thành Ngũ Thải nhan sắc, nhưng lại không có nửa điểm biện pháp.\r\n', '2023-02-28 16:23:44', 78, 45, 4, ''),
	(158, 'Ngắn ngủn không đến 10 phút, cốt cách trong cơ thể Tiêu Viêm cơ hồ hoàn toàn bị bao phủ bên trong năm màu nhan sắc, nhìn lại trong cơ thể, nơi nơi đều là tràn ngập năm màu sắc, có vẻ có chút quỷ dị.', '2023-02-28 16:23:48', 78, 56, 5, ''),
	(159, 'Ngay khi hành động của năng lượng làm Tiêu Viêm cảm thấy có một chút nghi hoặc, quả tim cũng đột nhiên đập mạnh vài cái, hắn cảm giác được, cốt cách bên trong, tựa hồ lúc này đột nhiên bốc cháy, ', '2023-02-28 16:23:56', 77, 49, 5, ''),
	(160, 'một cỗ cảm giác đau nhức xâm nhập tận xương tủy cấp tốc lan tràn mà ra, cuối cùng khuếch tán đến khắp ngõ ngách bên trong cơ thể.', '2023-02-28 16:24:01', 79, 51, 3, ''),
	(161, 'Cắn chặt răng, thân thể Tiêu Viêm ở bên trong chậu gỗ khẽ run rẩy, phía trên làn da cũng xuất hiện sự hồng nhuận khác thường, chỗ trán, mồ hôi lạnh dầy đặc, cuối cùng giống như một trận mưa nhỏ, theo khuôn mặ', '2023-02-28 16:24:12', 79, 49, 5, ''),
	(162, 'theo khuôn mặt chảy xuống, rơi vào trong chậu gỗ, hắn hiện giờ mới hiểu được, Dược Lão tại sao lại nói có một chút đau đớn, nhưng là, một ít đau đớn này chẳng phải có điểm thái quá sao?', '2023-02-28 16:24:23', 80, 51, 3, ''),
	(163, 'Đứng ở ngoài chậu gỗ, Dược Lão nhìn hành động như vậy của Tiêu Viêm, khóe mắt cũng nhịn không được khẽ giật, chợt khẽ thở dài một tiếng, thấp giọng nói:', '2023-02-28 16:24:26', 80, 51, 5, ''),
	(164, 'Lúc này đây Tiêu viêm cảm giác như đứng trong lò lửa, hơn nữa cỗ nhiệt hỏa này lại là từ bên trong nội thể bốc lên tạo thành cơn đau đớn kinh người, khiến cho hắn phải nghiến răng nghiến lợi mà chịu đựng, trong nhất thời mồ hôi lạnh đầy đầu.', '2023-02-28 16:28:14', 81, 46, 3, ''),
	(165, 'Cùng lúc đó nước thuốc ngũ sắc bên trong chậu gỗ càng ngày càng sôi trào dữ dội, dùng mắt thường còn có thể mơ hồ thấy từ trong nước thuốc 1 tia năng lượng thực chất cực kỳ nhỏ bé như có như không cấp tốc tiến đến bao phủ trên làn da Tiêu Viêm, nhưng chỉ trong giây lát rồi tiêu biến mất.', '2023-02-28 16:28:17', 81, 51, 4, ''),
	(166, 'Năng lượng cực mạnh ẩn dấu trong nước thuốc cho dù Tiêu Viêm hắn đã đoán trước được phần nào, nhưng hôm nay chân chính tự trải nghiệm vẫn khiến hắn âm thầm cảm thấy hoảng sợ, dù sao đây cũ', '2023-02-28 16:28:26', 82, 46, 5, ''),
	(167, 'ng là đã trải qua dược vật điều hòa rồi, nếu là trực tiếp phục dụng bản thân chết như thế nào cũng không biết, thật sự không hổ danh là thiên địa linh vật!', '2023-02-28 16:28:34', 82, 51, 5, ''),
	(168, 'Trong cơ thể, luồng năng lượng ngũ sắc dần dần từng chút từng chút trở nên trong suốt quỷ dị, thậm chí Tiêu Viêm có thể thấy rõ một luồng ngũ thải đó đang chảy cuồn cuộn bên trong cốt tủy. Từ bên ngoài luồng năng lượn', '2023-02-28 16:28:43', 83, 46, 5, ''),
	(169, 'bên trong nước thuốc cũng ko vì thể nội hắn chứa đầy mà e ngại, vẫn tiếp tục điên cuồng không ngừng từ bốn phía xâm nhập cở thể Tiêu Viêm, chỉ cần gặp phải cái gì mặc kệ là cốt cách, kinh mạch, thậm chí tế bào khí quan chờ một chút, đều không ngoại lệ toàn bộ bị chuyển hóa thành năm màu nhan sắc sặc sỡ.', '2023-02-28 16:28:45', 83, 51, 5, ''),
	(170, 'Trong lúc nhất thời, Tiêu Viêm tâm thần tập trung vào trogn cơ thể, cũng chỉ có thể ngơ ngác nhìn ngũ thải phân tán trong cơ thể, trong lòng ai thán một tiếng rồi vội vàng củng cố tâm thần, hắn biết rằng bây giờ cũng mới chỉ là bắt đầu. Y như Tiêu Viêm sở liệu', '2023-02-28 16:28:48', 84, 46, 5, ''),
	(171, ', tại thời điểm khi ngũ thải nhan sắc gần như lan tràn đến cả nửa thân thể, bỗng một cỗ nhiệt hỏa mãnh liệt bội phần so với lúc trước đột nhiên từ trong vòng kinh mạch, tế bào, cơ thể dần lan tràn mà ra. ', '2023-02-28 16:28:51', 84, 51, 4, ''),
	(172, 'Trong lúc nhất thời, cả người Tiêu viêm giống như bị đầu nhập vào trong Liệt Hỏa nóng hừng hực vậy, ngọn hỏa diễm kia tựa hồ hận không thể đem thân thể hắn đốt thành tro tàn, không chút lưu tình phóng thích nhiệt lượng càng ngày càng cao.', '2023-02-28 16:28:53', 85, 45, 5, ''),
	(173, 'Cùng với cơn đau bất ngờ xảy ra trong thể nội, Tiêu Viêm ngâm mình trên chậu gỗ cảm giác tại khoảnh khắc này toàn bộ bên trong cơ thể hắn như biến thành than hồng vậy, sương trắng nhè nhẹ từ đỉnh đầu hắ', '2023-02-28 16:28:55', 85, 51, 3, ''),
	(174, 'nh đầu hắn thẩm thấu mà ra, thậm chí bên trong làn sương còn ngửi đc mùi thịt cháy khen khét. Bộ mặt hắn lúc này không thể không biến dạng, hắn nghiến răng nghiến lợi đến nỗi từ khóe miệng trào ra một tia đỏ s', '2023-02-28 16:28:58', 86, 45, 5, ''),
	(175, 'thẩm thấu mà ra, thậm chí bên trong làn sương còn ngửi đc mùi thịt cháy khen khét. Bộ mặt hắn lúc này không thể không biến dạng, hắn nghiến răng nghiến lợi đến nỗi từ khóe miệng trào ra một tia ', '2023-02-28 16:29:01', 86, 51, 5, ''),
	(176, 'Dược Lão mặc dù biết tẩy tủy luyện cốt thật sự là cực kỳ đau đớn, nhưng dù sao cũng chưa bao giờ dùng qua Địa Tâm Thối Thể Nhũ cho nên cũng không thể biết chính xác cảm giác lúc này của Tiêu Viêm. ', '2023-02-28 16:29:09', 87, 45, 4, ''),
	(177, ' Nhưng hiện tại nhìn tình huống của hắn, lão hiểu mình đã quá coi nhẹ sự đau đớn này. Tuy nhiên việc đã như vậy, lão cũng chỉ có thể âm thầm cầu nguyện Tiêu Viêm có thể kiên trì tới cùng, không còn phương pháp nào khác.', '2023-02-28 16:29:14', 87, 51, 5, ''),
	(178, 'Đau, ngoại trừ đau vẫn là đau, thân thể Tiêu viêm lúc này khắp nơi đều truyền tới cảm giác đau nhức, mà đối với hắn ngoại trừ cắn răng chịu đựng thì không có biện pháp nào', '2023-02-28 16:29:16', 88, 45, 5, ''),
	(179, 'Bất quá làm cho hắn có chút vui mừng là hắn có thể cảm giác được trong cơ thể đang lan tràn nhiệt lượng nóng rực cùng nỗi đau đớn kia, có một cỗ năng lượng cường đại đang tán phát ra từ những chỗ nóng rực đó.', '2023-02-28 16:29:20', 88, 56, 3, ''),
	(180, 'Hắn phát hiện trải qua nóng rực, trong cơ thể cốt cách, kinh mạch, tế bào, cơ bắp cơ hồ đều sinh ra biến hóa thoát thai hoán cốt kinh người, hiển nhiên quá trình tẩy tủy luyện cốt thật là thần diệu.', '2023-02-28 16:29:26', 89, 45, 5, ''),
	(181, '"Chịu đựng! "\r\n\r\nCắn răng rống lên 1 tiếng. Cứ như thế hồi lâu sau, Tiêu Viêm bỗng nhiên ngạc nhiên nhận ra rằng, trong cơ thể cỗ nhiệt hỏa đau đớn kia tựa hồ tiêu giảm đi khá nhiều. ', '2023-02-28 16:29:30', 89, 56, 3, ''),
	(182, ' Có lẽ trải qua thời gian chịu đựng hắn đã miễn cưỡng có một chút lực kháng cự. Lúc này, khi cảm giác dày vò thống khổ đã dần dần đi qua, Tiêu Viêm đã thực sự lâm vào trạng thái mơ mơ tỉnh tỉnh, hắn như không còn cảm giác được cả khái niệm thời gian nữa', '2023-02-28 16:29:36', 90, 45, 5, ''),
	(183, 'duy nhất có thể cảm giác được dưới quá trình nung nóng này, kinh mạch, tế bào, cơ bắp... đang từng chút từng chút một trở nên tinh túy và cứng cỏi.', '2023-02-28 16:29:40', 90, 56, 4, ''),
	(184, 'Bên trong sơn động, Dược lão hai tay bắt chéo sau lưng, đứng im bên ngoài chậu gỗ không nhúc nhích. Từ lúc Tiêu viêm đi vào chậu gỗ đến nay đã được ba ngày, vậy mà trong ba ngày này lão thậm chí không di động nửa bước, tất cả sự chú ý của lão đều đặt lên trên người Tiêu Viêm.', '2023-02-28 16:38:16', 91, 56, 5, ''),
	(185, '"Hưu", bỗng nhiên một đạo thất thải quang mang chợt hiện trong động khẩu, cuối cùng quay tròn trên chậu gỗ. Thôn Thiên Mãng toàn thân như lửa chợt đến sát Tiêu Viêm, trong ánh mắt hiện rõ sự lo lắng cực kỳ...', '2023-02-28 16:38:20', 91, 45, 5, ''),
	(186, '- Không cần lo lắng, hắn đã trải qua thời điêm hung hiểm nhất, từ lúc này hẳn đã thuận lợi hơn nhiều.', '2023-02-28 16:38:26', 92, 56, 5, ''),
	(187, 'Dược lão vừa cười cười vừa nói, tầm mắt chuyển dời đến bên trong chậu gỗ, lúc này nước thuốc bên trong đã trở nên nhạt đi rất nhiều.', '2023-02-28 16:38:29', 92, 56, 5, ''),
	(188, 'Làm như nghe hiểu được lời Dược Lão, Thôn Thiên Mãng hướng về phía lão lưỡi rắn phì phì thò ra thụt vào như để trả lời hay xác nhận gì đó, sau đó ve vẩy cái đuôi bò ra ngoài sơn động, nó cần phải giúp Tiêu Viêm hộ pháp, không để cái gì ảnh hưởng đến hắn lúc này. Bạn đang đọc truyện được lấy tại chấm cơm.', '2023-02-28 16:38:31', 93, 45, 4, ''),
	(189, 'Nhìn thấy Thôn Thiên Mãng bò ra ngoài. Ánh mắt Dược Lão trở lại trên người Tiêu Viêm, nhẹ giọng nói:\r\n\r\n- Xem ra sẽ nhanh thôi.', '2023-02-28 16:38:33', 93, 46, 5, ''),
	(190, 'Lúc này bên trong cơ thể Tiên Viêm cảm giác nóng hừng hực đã tiêu giảm bớt, cứ như vậy theo thời gian cuối cùng lặng yên biến mất.', '2023-02-28 16:38:36', 94, 56, 5, ''),
	(191, '. Cùng với sự biến mất đó, hắn đang trong trạng thái mơ mơ hồ hồ cũng đột nhiên thanh tỉnh lại, vội vàng tập trung vào trong cơ thể, hắn chợt kinh ngạc phát hiện ngũ thải nhan sắc vốn che kín trong cơ thể đã hoàn toàn biến mất', '2023-02-28 16:38:38', 94, 45, 3, ''),
	(192, 'Cơ thể, khung xương, kinh mạch đều mơ hồ tản ra ánh sáng nhàn nhạt rất kì dị, nhìn qua giống như bạch ngọc, nhưng cũng tạo cảm giác tựa hồ trong đó ẩn dấu một lực lượng vô cùng cường đạ', '2023-02-28 16:38:40', 95, 56, 4, ''),
	(193, 'Tâm thần thức tỉnh, giống như một cái chìa khóa chợt khởi động một bộ máy tinh vi vốn nằm yên trong cơ thể. Quá trình thoát thai hoán cốt lần đầu tiên vận chuyển.\r\n\r\n"Ầm! "', '2023-02-28 16:38:42', 95, 45, 5, ''),
	(194, 'Tiếng động trầm thấp khẽ vang lên, Tiêu Viêm kinh ngạc nhận thấy bên trong cơ thể từ mọi phía trào ra năng lượng khổng lồ khiến cho hắn nhất thời thất thần. ', '2023-02-28 16:38:44', 96, 56, 5, ''),
	(195, 'Trong khi hắn vẫn còn đang thất thần, từng đạo năng lượng tinh thuần khổng lồ chủ động tràn vào bên trong kinh mạch. ', '2023-02-28 16:38:46', 96, 46, 4, ''),
	(196, ' Nếu đổi là trước đây, với cường độ năng lượng như vậy ít nhất phải khiến kinh mạc quá tải sinh ra cảm giác đau đớn vậy mà bây giờ kinh mạch hắn thậm chí còn đồng loạt co rụt lại, đem cỗ năng lượng khổng lồ đó nhét hết vào không có nửa điểm quá tải khiến hắn không có cảm giác đau đớn nào.', '2023-02-28 16:38:49', 97, 47, 5, ''),
	(197, 'Cỗ năng lượng này cực kỳ khổng lồ, nhưng lại cực tinh thuần làm người ta cảm thấy kinh dị, thậm chí nó đã tinh thuần đến độ không còn gì để Tiêu Viêm luyện hóa nữa, ngoài ra cỗ năng lượng này lại rất dễ bảo, không có nửa điểm chống cự, điều khiển nó cơ hồ giống như điều khiển cánh tay vậy.', '2023-02-28 16:38:52', 97, 46, 5, ''),
	(198, 'Luồng năng lượng khổng lồ dọc theo lộ tuyến " Phần Quyết " vận chuyển, sau một chu thiên giống như lũ bất ngờ bùng nổ, ầm ầm nổ không ngừng vọt vào khi toàn trong đấu tinh.', '2023-02-28 16:38:58', 98, 47, 4, ''),
	(199, 'Tại đây loại năng lượng khổng lồ chảy vào bên dưới. Từng quả trứng đấu tinh lớn nhỏ lấy mắt thường có thể thấy được tốc độ bắt đầu tăng trưởng.', '2023-02-28 16:39:02', 98, 46, 5, ''),
	(200, 'Lúc này bên trong cơ thể Tiên Viêm cảm giác nóng hừng hực đã tiêu giảm bớt, cứ như vậy theo thời gian cuối cùng lặng yên biến mất.', '2023-02-28 16:39:48', 101, 47, 4, ''),
	(201, 'Tâm thần mang theo một chút kinh ngạc nhìn tốc độ tăng trưởng đấu tinh, Tiêu Viêm có thể cảm giác được rõ ràng trong đó, nơi chứa đựng đấu khí đang dần trở mình bành trướng. Dựa theo tốc độ này đột phá đến Cửu Tinh Đại Đấu Sư dường như là việc dễ như trở bàn tay.', '2023-02-28 16:40:20', 99, 47, 5, ''),
	(202, 'Cùng với việc đạo năng lượng đang chảy vào không biết mệt mỏi, một tiếng động rất nhỏ vang lên. Tiêu Viêm phát hiện đấu tinh hắn vốn đã đến cực hạn bỗng xuất hiện một phạm vi cực kỳ khổng lồ, mà ở nơi tiếng vang', '2023-02-28 16:40:22', 99, 46, 4, ''),
	(203, 'kỳ dị xuất hiện, từng quả trứng đấu tinh lớn nhỏ trong giây lát bành trướng cho tới khi đạt kích thước bằng đầu nắm tay mới ngưng lại, hơn nữa cường quang ngọc ngà không ngừng hiện ra chiếu sáng khí toàn giống như ban ngày.', '2023-02-28 16:40:25', 100, 47, 5, ''),
	(204, 'Cảm thụ được trong cơ thể đủ loại biến hóa, Tiêu Viêm hít sâu một hơi, hắn rõ ràng trong nhất thời hắn đã muốn đột phá bát tinh, thực sự trở thành một gã Cửu Tinh Đại Đấu Sư! Chỉ cần lại tiếp tục tiến thêm một bước, hắn liền có thể đi vào cấp bậc Đấu Linh!.', '2023-02-28 16:40:28', 100, 46, 3, ''),
	(205, 'Bởi vì cấp bậc đấu tinh đột nhiên bành trướng. Tiêu Viêm nhận thấy bên trong đấu tinh cảm giác đầy đủ ban đầu một lần nữa trở nên trống rỗng, tinh thần lập tức tập trung vận chuyển năng lượng tinh thuần bên trong kinh mạch vào trong đấu tinh.', '2023-02-28 16:43:54', 101, 46, 5, ''),
	(206, '"Đại Đấu Sư cùng Đấu Linh quả nhiên chênh lệch quá nhiều, xem ra muốn ngay lập tức đột phá đến Đấu Linh là không có khả năng." Trong lòng vụt qua ý niệm này, Tiêu Viêm tiếc hận thở dài một hơi, vừa muốn thu công trở ra,', '2023-02-28 16:43:59', 102, 46, 4, ''),
	(207, 'lại đột nhiên phát hiện một cỗ hấp lực tự động từ đấu tinh bên trong bạo dũng mà ra, mà theo hấp lực bùng nổ, nhất thời, một cỗ năng lượng cực kỳ mạnh mẽ, lại lần nữa từ ngoại giới tràn vào trong cơ thể.', '2023-02-28 16:44:03', 102, 47, 5, ''),
	(208, 'Bởi vì cấp bậc đấu tinh đột nhiên bành trướng. Tiêu Viêm nhận thấy bên trong đấu tinh cảm giác đầy đủ ban đầu một lần nữa trở nên trống rỗng, tinh thần lập tức tập trung vận chuyển năng lượng tinh thuần bên trong kinh mạch vào trong đấu tinh.', '2023-02-28 16:44:07', 103, 55, 3, ''),
	(209, 'lại đột nhiên phát hiện một cỗ hấp lực tự động từ đấu tinh bên trong bạo dũng mà ra, mà theo hấp lực bùng nổ, nhất thời, một cỗ năng lượng cực kỳ mạnh mẽ, lại lần nữa từ ngoại giới tràn vào trong cơ thể.', '2023-02-28 16:44:21', 104, 47, 5, ''),
	(210, '"Đại Đấu Sư cùng Đấu Linh quả nhiên chênh lệch quá nhiều, xem ra muốn ngay lập tức đột phá đến Đấu Linh là không có khả năng." Trong lòng vụt qua ý niệm này, Tiêu Viêm tiếc hận thở dài một hơi, vừa muốn thu công trở ra,', '2023-02-28 16:44:24', 105, 47, 5, ''),
	(211, 'Nhưng lúc sau đấu tinh lại giống như trước, xuất hiện cảm giác trống rỗng như một cái vực sâu không thấy đáy, từng đạo năng lượng quán nhập giống như đá chìm đáy biển vẫn chưa làm cho Tiêu Viêm cảm giác quá mức rõ ràng.', '2023-02-28 16:44:28', 105, 46, 4, ''),
	(212, 'Bởi vì cấp bậc đấu tinh đột nhiên bành trướng. Tiêu Viêm nhận thấy bên trong đấu tinh cảm giác đầy đủ ban đầu một lần nữa trở nên trống rỗng, tinh thần lập tức tập trung vận chuyển năng lượng tinh thuần bên trong kinh mạch vào trong đấu tinh.', '2023-02-28 16:44:32', 106, 47, 5, ''),
	(213, '"Đại Đấu Sư cùng Đấu Linh quả nhiên chênh lệch quá nhiều, xem ra muốn ngay lập tức đột phá đến Đấu Linh là không có khả năng." Trong lòng vụt qua ý niệm này, Tiêu Viêm tiếc hận thở dài một hơi, vừa muốn thu công trở ra,', '2023-02-28 16:44:35', 107, 55, 5, ''),
	(214, 'Nhưng lúc sau đấu tinh lại giống như trước, xuất hiện cảm giác trống rỗng như một cái vực sâu không thấy đáy, từng đạo năng lượng quán nhập giống như đá chìm đáy biển vẫn chưa làm cho Tiêu Viêm cảm giác quá mức rõ ràng.', '2023-02-28 16:44:38', 106, 47, 5, ''),
